import { render } from '@testing-library/react';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';
import { element } from 'prop-types';
import { ReactVideo } from "reactjs-media";

let currentTitle = `The Movie List`;

const currentList = []

const title = <h1 className="App-title">{currentTitle}</h1>;

const App = () => {
  const [popular, setPopular] = useState(false)
  const [topMovies, setTopmovies] = useState(false)
  const [upcomingMovies, setUpcomingMovies] = useState(true)
  const [desc, setDescription] = useState(false)
  let [currentList, setList] = useState([])
  let [movieNumber, setNumber] = useState(0);

  const ActivatePopular = () => {
    setPopular((
      popular, true
    ))

    setTopmovies((
      topMovies, false
    ))

    setUpcomingMovies((
      upcomingMovies, false
    ))
  }

  const ActivateTopMovies = () => {
    setTopmovies((
      topMovies, true
    ))

    setUpcomingMovies((
      upcomingMovies, false
    ))

    setPopular((
      popular, false
    ))
  }

  const ActivateUpcoming = () => {
    setUpcomingMovies((
      upcomingMovies, true
    ))

    setTopmovies((
      topMovies, false
    ))

    setPopular((
      popular, false
    ))
  }

  const ActivateDescription = () => {
    setDescription((
      desc, true
    ))
  }

  useEffect(() => {

    if (popular)
    {
      axios.get('https://api.themoviedb.org/3/movie/popular?api_key=1b34b56c896270b1a9bdd7563b01f45d').then(res => {

      setList(
        res.data.results
      )

      })
    }
    else if (topMovies)
    {
      axios.get('https://api.themoviedb.org/3/movie/top_rated?api_key=1b34b56c896270b1a9bdd7563b01f45d').then(res => {

      setList(
        res.data.results
      )

      console.log(res.data)

      })
    }
    else if (upcomingMovies)
    {
      axios.get('https://api.themoviedb.org/3/movie/upcoming?api_key=1b34b56c896270b1a9bdd7563b01f45d').then(res => {

      setList(
        res.data.results
      )

      console.log(res.data)

      })
    }

  }, [popular, topMovies, upcomingMovies] )

    console.log(currentList[movieNumber])

  const MovieInfo = (props) => {
    return (
      <ol className="Movie-list">
        <li className="Movie-list">
        <h1 className="Movie-title"> {props.title} </h1>

        <div className="Movie-background">
          <img src={props.poster_path} className="Movie-image" onMouseEnter={() => setDescription(true)} onMouseLeave={() => setDescription(false)}></img>
          <img src="https://upload.wikimedia.org/wikipedia/commons/9/99/Star_icon_stylized.svg" className="Star-icon"></img><p className="Movie-rating">{props.vote_average}/10</p>

          {desc &&(
            <div className="Description-window">
            <br/>
            <br/>
            <br/>
            <br/>
            <br/>
            <br/>
            <h1>Description</h1>
            <p>{props.overview}</p>
          </div>
          )}
        </div>

        {description}

      </li>
      </ol>
    )
  }

  return (
    <div className="App">
      <header className="App-header">
        {title}
        <ol className="Categorie-list">
          <li className="Categorie-list">
            <button onClick={ActivatePopular}>

              Popular

            </button>
          </li>
          <li className="Categorie-list">
            <button onClick={ActivateTopMovies}>

              Top Movies

            </button>
          </li>
          <li className="Categorie-list">
            <button onClick={ActivateUpcoming}>

              Upcoming

            </button>
          </li>
        </ol>

      </header>

      <div className="movie-titles">
          {currentList.map((list) => {
            return (
              <MovieInfo title={list.title} poster_path={'https://image.tmdb.org/t/p/w500/' + list.poster_path} vote_average={list.vote_average} overview={list.overview}/>
            );
          })}
      </div>

    </div>

  );
}

export default App;
